<script>
    const msgerForm = get("".msger-inputarea"");
    const msgerInput = get("".msger-input"");
    const msgerChat = get("".msger-chat"");

    const BOT_MSGS = [
        ""Hi, how are you?"",
        ""Oh nee je bent retarded"",
    ];

    // Icons made by");
            WriteLiteral(@" Freepik from www.flaticon.com
    const BOT_IMG = ""https://media.discordapp.net/attachments/749915557045993537/797116877376716840/image.png?width=676&height=676"";
    const PERSON_IMG = ""https://media.discordapp.net/attachments/749915557045993537/797117250393079868/image.png?width=676&height=676"";
    const BOT_NAME = ""Geert"";
    const PERSON_NAME = ""Ongemakelijk-man"";

    msgerForm.addEventListener(""submit"", event => {
        event.preventDefault();

        const msgText = msgerInput.value;
        if (!msgText) return;

        appendMessage(PERSON_NAME, PERSON_IMG, ""right"", msgText);
        msgerInput.value = """";

        botResponse();
    });

    function appendMessage(name, img, side, text) {
        //   Simple solution for small apps
        const msgHTML = `
    <div class=""msg ${side}-msg"">
      <div class=""msg-img"" style=""background-image: url(${img})""></div>

      <div class=""msg-bubble"">
        <div class=""msg-info"">
          <div class=""msg-info-name"">${name}");
            WriteLiteral(@"</div>
          <div class=""msg-info-time"">${formatDate(new Date())}</div>
        </div>

        <div class=""msg-text"">${text}</div>
      </div>
    </div>
  `;

        msgerChat.insertAdjacentHTML(""beforeend"", msgHTML);
        msgerChat.scrollTop += 500;
    }

    function botResponse() {
        const r = random(0, BOT_MSGS.length - 1);
        const msgText = BOT_MSGS[r];
        const delay = msgText.split("" "").length * 100;

        setTimeout(() => {
            appendMessage(BOT_NAME, BOT_IMG, ""left"", msgText);
        }, delay);
    }

    // Utils
    function get(selector, root = document) {
        return root.querySelector(selector);
    }

    function formatDate(date) {
        const h = ""0"" + date.getHours();
        const m = ""0"" + date.getMinutes();

        return `${h.slice(-2)}:${m.slice(-2)}`;
    }

    function random(min, max) {
        return Math.floor(Math.random() * (max - min) + min);
    }

</script>");
